      <div class="subTitle"></div>

      <div class="btns">
        <a class="button" @click="onClickWriteCard">Ïπ¥Îìú Ïì∞Í∏∞</a>
        <a class="button" @click="onClickMyCard">ÎßàÏù¥ÌéòÏù¥ÏßÄ</a>
      </div>
    </div>
