<script>
import SplashTreeVue from "@/components/SplashTree.vue";

export default {
  name: "MainView",
  components: {
    SplashTreeVue,
  },
  async mounted() {
    this.$refs.SplashTreeVue.show("");
    const subTitle = document.querySelector(".subTitle");
    await this.type(subTitle, "ÌÅ¨Î¶¨Ïä§ÎßàÏä§ Ïπ¥ÎìúÎ°ú ÎßàÏùåÏùÑ Ï†ÑÌï¥Ï£ºÏÑ∏Ïöî", 75);
    subTitle.innerHTML += "üíõ";
  },
  methods: {
    onClickLogin() {
      const { Kakao } = window;
      Kakao.Auth.authorize({
        redirectUri: process.env.VUE_APP_KAKAO_REDIRECT_URI,
      });
    },
    toKorChars(str) {
      var cCho = [
          "„Ñ±",
          "„Ñ≤",
          "„Ñ¥",
          "„Ñ∑",
          "„Ñ∏",
          "„Ñπ",
          "„ÖÅ",
          "„ÖÇ",
          "„ÖÉ",
          "„ÖÖ",
          "„ÖÜ",
          "„Öá",
          "„Öà",
          "„Öâ",
          "„Öä",
          "„Öã",
          "„Öå",
          "„Öç",
          "„Öé",
        ],
        // cJung = ['„Öè', '„Öê', '„Öë', '„Öí', '„Öì', '„Öî', '„Öï', '„Öñ', '„Öó', '„Öò', '„Öô', '„Öö', '„Öõ', '„Öú', '„Öù', '„Öû', '„Öü', '„Ö†', '„Ö°', '„Ö¢', '„Ö£'],
        cJong = [
          "",
          "„Ñ±",
          "„Ñ≤",
          "„Ñ≥",
          "„Ñ¥",
          "„Ñµ",
          "„Ñ∂",
          "„Ñ∑",
          "„Ñπ",
          "„Ñ∫",
          "„Ñª",
          "„Ñº",
          "„ÑΩ",
          "„Ñæ",
          "„Ñø",
          "„ÖÄ",
          "„ÖÅ",
          "„ÖÇ",
          "„ÖÑ",
          "„ÖÖ",
          "„ÖÜ",
          "„Öá",
          "„Öà",
          "„Öä",
          "„Öã",
          "„Öå",
          "„Öç",
          "„Öé",
        ],
        cho,
        jung,
        jong;
      var cnt = str.length,
        chars = [],
        cCode;
      for (var i = 0; i < cnt; i++) {
        cCode = str.charCodeAt(i);
        if (cCode == 32) {
          chars.push(" ");
          continue;
        } // ÌïúÍ∏ÄÏù¥ ÏïÑÎãå Í≤ΩÏö∞
        if (cCode < 0xac00 || cCode > 0xd7a3) {
          chars.push(str.charAt(i));
          continue;
        }
        cCode = str.charCodeAt(i) - 0xac00;
        jong = cCode % 28;
        jung = ((cCode - jong) / 28) % 21;
        cho = ((cCode - jong) / 28 - jung) / 21;
        chars.push(cCho[cho]);
        chars.push(String.fromCharCode(44032 + cho * 588 + jung * 28));
        if (cJong[jong] !== "") {
          chars.push(String.fromCharCode(44032 + cho * 588 + jung * 28 + jong));
        }
      }
      return chars;
    },
    async type(target, msg, ms) {
      const splitedMsg = msg.split('\n');
      for(let k=0; k<splitedMsg.length; k++) {
        let str = splitedMsg[k];
        let typeing1 = [];
        str = str.split("");

        for (let i = 0; i < str.length; i++) {
          typeing1[i] = this.toKorChars(str[i]);
        }
        const length = str.length;

        for (let i = 0; i < length; i++) {
          const oneChar = typeing1[i];

          const originHTML = target.innerHTML;
          for (let j = 0; j < oneChar.length - 1; j++) {
            target.innerHTML = originHTML + oneChar[j];
            await this.delay(ms);
          }
          target.innerHTML = originHTML + oneChar[oneChar.length - 1];
          await this.delay(ms);
        }
        if(k !== splitedMsg.length - 1) target.innerHTML += '<br />';
      }
    },
    delay(ms) {
      return new Promise((resolve) => setTimeout(resolve, ms));
    },
    onClickMyCard() {
      if(this.checkLogin()) {
        this.$router.push({ name: "MyPage" });
      }
    },
    onClickWriteCard() {
      if(this.checkLogin()) {
        this.$router.push({ name: "Write" });
      }
    },
    checkLogin() {
      if(!this.$store.state.user) {
        const result = confirm("Ïπ¥Ïπ¥Ïò§ÌÜ° Í∞ÑÌé∏Î°úÍ∑∏Ïù∏ÏúºÎ°ú ÏÑúÎπÑÏä§ Ïù¥Ïö©Í∞ÄÎä•Ìï©ÎãàÎã§.\nÎ°úÍ∑∏Ïù∏ ÌïòÏãúÍ≤†ÏäµÎãàÍπå ?");
        if(result) this.onClickLogin();
      } else {
        return true;
      }
    },
  },
};
</script>

